  overflow-x: scroll;
  overflow-y: hidden;
  margin-top: -50vh;
  margin-bottom: -50vh;
  padding-top: 50vh;
  padding-bottom: 50vh;
}
.scroll-container-x.segment::-webkit-scrollbar {
  display: none;
}
.scroll-container-x.screen {
  overflow-x: scroll;
  overflow-y: hidden;
  margin-top: -100vh;
  margin-bottom: -100vh;
  padding-top: 100vh;
  padding-bottom: 100vh;
}
.scroll-container-x.screen::-webkit-scrollbar {
  display: none;
}

.scroll-container-y, .scroll-container-y.bit {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -1rem;
  margin-right: -1rem;
  padding-left: 1rem;
  padding-right: 1rem;
}
.scroll-container-y::-webkit-scrollbar, .scroll-container-y.bit::-webkit-scrollbar {
  display: none;
}
.scroll-container-y.chunk {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -4rem;
  margin-right: -4rem;
  padding-left: 4rem;
  padding-right: 4rem;
}
.scroll-container-y.chunk::-webkit-scrollbar {
  display: none;
}
.scroll-container-y.partial {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -15vw;
  margin-right: -15vw;
  padding-left: 15vw;
  padding-right: 15vw;
}
.scroll-container-y.partial::-webkit-scrollbar {
  display: none;
}
.scroll-container-y.segment {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -50vw;
  margin-right: -50vw;
  padding-left: 50vw;
  padding-right: 50vw;
}
.scroll-container-y.segment::-webkit-scrollbar {
  display: none;
}
.scroll-container-y.screen {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -100vw;
  margin-right: -100vw;
  padding-left: 100vw;
  padding-right: 100vw;
}
.scroll-container-y.screen::-webkit-scrollbar {
  display: none;
}

.wrap-container {
  display: flex;
  align-items: flex-start;
  justify-content: flex-start;
  flex-wrap: wrap;
}
.wrap-container > * {
