:root {
  --navbar-height: 10em;
  --navbar-max-height: 8em;
}

body {
  display: grid;
  justify-content: stretch;
  align-content: flex-start;
  gap: 0;
}
#fixed-screen {
  pointer-events: none;
  display: inline-block;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1000;
}

@media screen and (min-width: 0) {
  #navbar {
    border: var(--line-width) solid var(--col-grey);
    /* width: 100%; */
    background-color: var(--col-white);
    box-shadow: var(--drop-out);
    z-index: 1;
  }
  #navbar > .content {
    position: relative;
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    box-sizing: border-box;
    margin: auto auto;
    width: 100%;
    max-width: var(--max-article-width);
  }
  #navbar > .content > * {
    position: relative;
    flex-basis: 100%;
  }
  #navbar > .content > *.left-navbar {
    position: relative;
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
    padding: 1rem;
    z-index: 1;
  }
  #navbar > .content > *.left-navbar #navbar-logo {
    cursor: pointer;
    width: 2cm;
  }
  #navbar > .content > *.left-navbar .toggle {
    cursor: pointer;
    user-select: none;
    -webkit-tap-highlight-color: transparent;
    appearance: none;
    margin: 1rem;
    width: 1cm;
    height: 1cm;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
  }
  #navbar > .content > *.right-navbar {
    position: relative;
    flex-shrink: 1;
    flex-grow: 1;
    z-index: 0;
  }
  #navbar > .content > *.right-navbar .content {
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: stretch;
    text-align: center;
  }
  #navbar > .content > *.right-navbar .content > * {
    position: relative;
    display: inline-block;
    flex-shrink: 0;
    text-decoration: none;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(1) {
    z-index: 0;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(2) {
    z-index: 1;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(3) {
    z-index: 2;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(4) {
    z-index: 3;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(5) {
    z-index: 4;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(6) {
    z-index: 5;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(7) {
    z-index: 6;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(8) {
    z-index: 7;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(9) {
    z-index: 8;
  }
  #navbar > .content > *.right-navbar .content > *:nth-last-child(10) {
    z-index: 9;
  }
  #navbar > .content > *.right-navbar .content > * > h2 {
    color: var(--col-purple-dark);
    text-decoration: none;
  }
}
@media screen and (max-width: 719px) {
  #navbar > .content > *.left-navbar {
    background-color: var(--col-white);
    box-shadow: var(--drop-out);
  }
  #navbar > .content > *.right-navbar:not(.-active) {
    display: none;
  }
  #navbar > .content > *.right-navbar .content > * {
    background-color: var(--col-white);
    box-shadow: var(--drop-out);
  }
  #navbar > .content > *.right-navbar .content > *:hover {
    background-color: var(--col-grey-light);
  }
}
@media screen and (min-width: 720px) {
  #navbar > .content {
    row-gap: auto;
    align-items: stretch;
  }
  #navbar > .content > * {
    flex-basis: auto;
  }
  #navbar > .content > *.left-navbar #navbar-logo {
    flex-grow: 1;
    height: 4cm;
    width: auto;
  }
  #navbar > .content > *.left-navbar .toggle {
    display: none;
  }
  #navbar > .content > *.right-navbar .content {
    flex-direction: row;
    justify-content: flex-end;
    align-items: stretch;
    column-gap: 2em;
    box-sizing: border-box;
    height: 100%;
    padding: 1rem;
  }
  #navbar > .content > *.right-navbar .content > * {
    display: flex;
    align-items: center;
    border: none;
  }
}

#footer {
  height: 2cm;
  background-color: var(--col-purple-dark);
}
#footer > .content {
  box-sizing: border-box;
  margin: auto auto;
  width: 100%;
  max-width: var(--max-article-width);
  padding: 1em;
}
#footer .description {
  text-align: center;
}
@media screen and (min-width: 0) {
  .book-player-info {
    position: relative;
    padding: 1em 1em;
  }
  .book-player-info > div {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    align-items: center;
    max-width: var(--max-info-width);
    justify-content: space-between;
    gap: 1em;
  }
  .book-player-info .image {
    position: relative;
    margin-bottom: 0.5rem;
  }
  .book-player-info .image img {
    display: inline-block;
    vertical-align: top;
    filter: drop-shadow(var(--drop-out));
  }
  .book-player-info .info {
    position: relative;
    flex-basis: 0;
    flex-grow: 1;
    flex-shrink: 1;
  }
  .book-player-info .info > .title {
    margin: 0 auto;
    text-align: center;
    text-decoration: none;
    color: inherit;
  }
  .book-player-info .info > hr {
    display: none;
    border-style: solid;
  }
  .book-player-info .info > .tags {
    display: flex;
    justify-content: center;
    align-items: flex-start;
    flex-wrap: wrap;
    column-gap: 0.4em;
    row-gap: 0.4em;
    margin-top: 0.5rem;
    margin-bottom: 1rem;
  }
  .book-player-info .info > .tags > * {
    flex-shrink: 1;
    flex-grow: 1;
    border: var(--line-width) solid;
    padding: 2px 1em;
    font-weight: bold;
    text-align: center;
    text-decoration: none;
  }
  .book-player-info .info > .description {
    max-width: 75ch;
    max-height: var(--max-info-desc);
  }
  .book-player-info .info > .description * {
    color: inherit;
  }
  .book-player-info .info > .drawer {
    display: none;
  }
}
@media screen and (min-width: 480px) {
  .book-player-info > div {
    flex-direction: row;
    justify-content: center;
    align-items: flex-start;
  }
  .book-player-info .image {
    margin-bottom: 0;
    width: var(--min-book-info);
  }
  .book-player-info .image img {
    float: right;
  }
  .book-player-info .info > .title {
    text-align: left;
    margin-bottom: -2px;
    border: var(--line-width) solid;
    padding: 0.5rem;
  }
  .book-player-info .info > .tags {
    justify-content: flex-start;
    column-gap: 0.5em;
    row-gap: 0.25em;
    margin-top: 0;
    margin-bottom: 1em;
    padding-left: 1em;
    padding-right: 1em;
  }
  .book-player-info .info > .tags > * {
    flex-grow: 0;
  }
  .book-player-info .info > .description {
    padding-left: 1em;
  }
}
@media screen and (min-width: 960px) {
  .book-player-info .image {
    width: var(--max-book-info);
  }
}
.book-player-info.-overflow {
  padding-bottom: 1em !important;
}
.book-player-info.-overflow .info > .description {
  overflow-y: hidden;
  mask-image: linear-gradient(to bottom, white calc(100% - 6em), var(--end-col));
  -webkit-mask-image: linear-gradient(to bottom, white calc(100% - 6em), var(--end-col));
}
.book-player-info.-overflow .info > .drawer {
  cursor: pointer;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
  overflow-y: hidden;
  position: relative;
  display: block;
  margin-top: 1em;
  width: 100%;
  height: 2em;
}
.book-player-info.-overflow .info > .drawer .text {
  pointer-events: none;
  position: absolute;
  bottom: -50%;
  margin-top: auto;
  width: 100%;
  text-align: center;
  color: hsl(var(--colm-white), 60%);
  font-weight: bold;
  font-size: 0.75rem;
  font-style: italic;
  letter-spacing: 0.5ch;
  transform: translateY(50%);
  transition: bottom 200ms ease;
}
.book-player-info.-overflow .info > .drawer:hover {
  background: radial-gradient(ellipse farthest-side at center bottom, var(--drop-col), transparent);
}
.book-player-info.-overflow .info > .drawer:hover .text {
  bottom: 50%;
  transition: bottom 100ms ease;
}
.book-player-info.-overflow .info > .drawer:active {
  background: hsl(var(--colm-grey-light), 10%);
}
@media (hover: none) {
  .book-player-info.-overflow .info > .drawer {
    background: radial-gradient(ellipse farthest-side at center bottom, var(--drop-col), transparent);
  }
  .book-player-info.-overflow .info > .drawer .text {
    bottom: 50%;
    transition: bottom 100ms ease;
  }
}
.book-player-info.-overflow:not(.-expand) .info > .drawer .text::after {
  content: "EXPAND";
}
.book-player-info.-overflow:not(.-expand) .info > .description {
  height: var(--max-info-desc);
  max-height: var(--max-info-desc);
  --end-col: hsla(0, 0%, 100%, 0%);
  transition: height 1000ms ease, max-height 1000ms ease, --end-col 1000ms;
}
.book-player-info.-overflow.-expand .info > .drawer .text::after {
  content: "COLLAPSE";
}
.book-player-info.-overflow.-expand .info > .description {
  height: var(--min-info-desc);
  max-height: var(--min-info-desc);
  --end-col: hsl(0, 0%, 100%, 100%);
  transition: height 1000ms ease, max-height 1000ms ease, --end-col 1000ms;
}

.book-player-info.book {
  padding-top: 2em;
  padding-bottom: 2em;
  background-color: var(--col-purple-dark);
}
@media screen and (min-width: 0) {
  .book-player-info.book .image img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
}
@media screen and (max-width: 479px) {
