@media screen and (min-width: 480px) {
  .book-player-info.book .image {
    height: var(--min-book-info);
  }
  .book-player-info.book .image img {
    object-position: top right;
    z-index: 1;
  }
}
@media screen and (min-width: 960px) {
  .book-player-info.book .image {
    height: var(--max-book-info);
  }
}

.book-player-info.author {
  background-color: var(--col-gold-dim);
}
@media screen and (min-width: 0) {
  .book-player-info.author .image img {
    border-radius: 100%;
    height: 3cm;
    width: 3cm;
    object-fit: cover;
    object-position: center;
  }
