}
@media screen and (min-width: 0) {
  .book-player-info.reader .image img {
    border-radius: 100%;
    height: 2cm;
    width: 2cm;
    object-fit: cover;
    object-position: center;
  }
}
@media screen and (min-width: 480px) and (max-width: 959px) {
  .book-player-info.reader .image img {
    width: 2cm;
    height: 2cm;
  }
}

.book-player-info.editor {
  background-color: var(--col-white);
  box-shadow: none;
  border-bottom: var(--line-width) solid var(--col-grey-light);
  padding-top: 1cm;
  padding-bottom: 1cm;
}
.book-player-info.editor .title::before {
  content: "Edited by ";
