    background: linear-gradient(to right, var(--range-fill) 0%, var(--range-fill) var(--range-progress), var(--range-remain) var(--range-progress), var(--range-remain) 100%);
}

.-live-area {
    // --max-area:100px;
    // --min-area: 100px;
}

sources.scss\\resources\\appearance\\_effects.sc:
@use "./colours";

:root {
    --line-width: 2px;
    --line-major-width: 4px;
}

:root {
    --drop-col: rgb(0, 0, 0, 0.2);
    --drop-out: 1px 1px 12px var(--drop-col); //--drop-out: 1px 1px 12px rgb(0, 0, 0, 0.5);
    --drop-in: inset var(--drop-out);
}


@function --area-gradient($direction: "to right") {
    @return linear-gradient($direction, #00000022, transparent);
}

@mixin drop-out($img: null) {
    @if $img == null {
        @include drop-box-out();
    } @else {
        @include drop-img-out();
    }
}
@mixin drop-in($img: null) {
    @if $img == null {
        @include drop-box-in();
    } @else {
        @include drop-img-in();
    }
}
@mixin drop-box-out {
    box-shadow: var(--drop-out);
}
@mixin drop-box-in {
    box-shadow: var(--drop-in);
}
@mixin drop-img-out {
    filter:drop-shadow(var(--drop-out));
}
@mixin drop-imh-in {
    filter:drop-shadow(var(--drop-in));
}

sources.scss\\resources\\layout\\_breakpoints.sc:
@use "../../helpers/functions";
@use "../../helpers/mixins";

//https://youtu.be/3R4fpnIAozs?list=PL1sJZ9qwpVPbPqM5dTnP_L1R96eaoQhNK
//https://www.w3schools.com/howto/howto_css_media_query_breakpoints.asp

$breakpoints: (
    xs: 0,          //extra small       (phones)
    sm: 480px,      //small             (large phones, tablets)
    md: 720px,      //medium            (large tablets)
    lg: 960px,      //large             (laptops, desktops)
    xl: 1200px,     //extra large       (large laptops, large desktops) 
    gg: 1600px      //gigantic          (TVs)
);

@function get-min($breakpoint) {
    @return map-get($breakpoints, $breakpoint);
}

@function get-max($breakpoint, $decrement: true) {
    $index: functions.map-index($breakpoints, $breakpoint) + 1;
    @if ($index > functions.map-length($breakpoints)) {
        $index: functions.map-length($breakpoints);
    }

    $point: functions.map-nth($breakpoints, $index, true);
    @if ($decrement == true) {
        $point: $point - 1;
    }

    @return $point;
}

@function get-avg($breakpoint) {
    @return calc(#{get-min($breakpoint) + get-max($breakpoint, null)} / 2);
}

@function get($breakpoint, $value: 0, $decrement: false) {
    @return calc(#{get-min($breakpoint) + get-max($breakpoint, $decrement)} * #{$value});
}

@mixin default($selector: null) {
    ._layout-default #{unquote(#{$selector})} {
        @content;
    }
}

//TOOLS
@mixin xs($below: null) {
    @include breakpoint (xs, $below) {
        @content;
    }
}

@mixin sm($below: null) {
    @include breakpoint (sm, $below) {
        @content;
    }
}

@mixin md($below: null) {
    @include breakpoint (md, $below) {
        @content;
    }
}

@mixin lg($below: null) {
    @include breakpoint (lg, $below) {
        @content;
    }
}

@mixin xl($below: null) {
    @include breakpoint (xl, $below) {
        @content;
    }
}

@mixin breakpoint($breakpoint: xs, $below: null) {
    @if ($below == null) {
        @include mixins.custom-breakpoint(get-min($breakpoint)) {
            @content;
        }
    } @else {
        @include mixins.custom-breakpoint(get-max($breakpoint), $below) {
            @content;
        }
    }
}

@mixin between($min, $max) {
    @include mixins.custom-breakpoint(get-min($min)) {
        @include mixins.custom-breakpoint(get-max($max), true) {
            @content;
        }
    }
}

@mixin min {
    @media screen and (min-width: 0) {
        @content;
    }
}

@mixin max {
    @media screen and (max-width: 100vw) {
        @content;
    }
}

.responsive-test {
    &::before {
        content: "Breakpoint Test: ";
    }

    &::after {
        content: "N/A";

        @include xs {
            color: purple;
            content: "Extra Small (xs)";
        }
        @include sm {
            color: blue;
            content: "Small (sm)";
        }
        @include md {
            color: green;
            content: "Medium (md)";
        }
        @include lg {
            color: orange;
            content: "Large (lg)";
        }
        @include xl {
            color: red;
            content: "Extra Large (xl)";
        }
        @include mixins.custom-breakpoint(1400px) {
            color: brown;
            content: "GIGANTIC";
        }
    }
}

.responsive-test-below {
    &::before {
        content: "Breakpoint Test (Below): ";
    }

    &::after {
        content: "N/A";

        @include xl(true) {
            color: red;
            content: "Extra Large (xl)";
        }
        @include lg(true) {
            color: orange;
            content: "Large (lg)";
        }
        @include md(true) {
            color: green;
            content: "Medium (md)";
        }
        @include sm(true) {
            color: blue;
            content: "Small (sm)";
        }
        @include xs(true) {
            color: purple;
            content: "Extra Small (xs)";
        }
    }
}

sources.scss\\helpers\\_mixins.scss:
@use "./variables";
@use "./functions";

//CUSTOM ELEMENT SELECTOR

//selector (a b, c d) --> parse (a b) (c d) --> element ((a) (b)) ((c) (d))
@mixin custom-selector($rule, $selectors: "@at-root #{&}") {
    $at-root: false;
    @if (str-index($selectors, "@at-root") != null) {
        $at-root:true;

        $selectors: str-slice($selectors, str-length("@at-root") + 2);
    }

    @each $parse in selector-parse($selectors) { //split commas (a, b) => (a) (b)
        // @debug "======================================================";
        // @debug "BEFORE --- [#{$parse}]";
        $parse: functions.selector-split($parse);
        // @debug "AFTER --- [#{$parse}]";
        // @debug length($parse);

        @for $selector-index from 1 through length($parse) { //split nested (a b) => (a) (b)
            // @debug "-------------------";
            // @debug "[#{$parse}] (#{length($parse)})";
            $selector: nth($parse, $selector-index);

            $new-parse-1: "*";
            $new-parse-2: "*";
            @for $element-index from 1 through length($parse) { //split nested (a b) => (a) (b)
                $element: nth($parse, $element-index);
                
                @if ($selector-index == $element-index) {
                    $element-1: selector-unify("*", $rule);
                    $element-2: selector-unify($element, $rule);
                    
                    @if ($element-1 != null) {
                        $new-parse-1: selector-nest(selector-nest($new-parse-1, $element-1), $element);
                    }
                    @if ($element-2 != null) {
                        $new-parse-2: selector-nest($new-parse-2, $element-2);
                    }
               } @else {
                    $new-parse-1: selector-nest($new-parse-1, $element);
                    $new-parse-2: selector-nest($new-parse-2, $element);
                }
            }

            @each $new-selector-1 in $new-parse-1 {
                $new-selector-1: str-slice(inspect($new-selector-1), str-length("*") + 2);
            
                // @debug $new-selector-1;

                @if ($at-root == false) {
                    #{unquote(#{$new-selector-1})} {
                        @content;
                    }
                } @else if ($at-root == true) {
                    @at-root #{unquote(#{$new-selector-1})} {
                        @content;
                    }
                }
            }
            @each $new-selector-2 in $new-parse-2 {
                $new-selector-2: str-slice(inspect($new-selector-2), str-length("*") + 2);
            
                // @debug $new-selector-2;

                @if ($at-root == false) {
                    #{unquote(#{$new-selector-2})} {
                        @content;
                    }
                } @else if ($at-root == true) {
                    @at-root #{unquote(#{$new-selector-2})} {
                        @content;
                    }
                }
            }
        }
    }
}

//BREAKPOINT SELECTOR
@mixin custom-breakpoint($amount: 0, $below: null) {
    @if ($below == null) {
        @media screen and (min-width: $amount) {
            @content;
        }
    } @else {
        @media screen and (max-width: $amount) {
            @content;
        }
    }
}

//SCROLL CONTAINERS
//for small mobile devices (invisible scrollbars, touch-screen scrolling for large content)
@mixin scroll-container-x ($reserve-top: 1em, $reserve-bottom: 1em) {
    overflow-x: scroll;
    overflow-y:hidden;

    margin-top: $reserve-top * -1;
    margin-bottom: $reserve-bottom * -1;
    padding-top: $reserve-top;
    padding-bottom: $reserve-bottom;

    &::-webkit-scrollbar {
        display: none;
    }
}

@mixin scroll-container-y ($reserve-left: 1em, $reserve-right: 1em) {
    overflow-x:hidden;
    overflow-y: scroll;
    
    margin-left: $reserve-left * -1;
    margin-right: $reserve-right * -1;
    padding-left: $reserve-left;
    padding-right: $reserve-right;

    &::-webkit-scrollbar {
        display: none;
    }
}

//Input Cursor
@mixin screen-mouse { /* mouse, touch pad */
    @media (hover: hover) and (pointer: fine) {
        @content;
    }
}
@mixin screen-stylus { /* stylus-based screens */
    @media (hover: none) and (pointer: fine) {
        @content;
    }
}
@mixin screen-touch { /* smartphones, touchscreens */
    @media (hover: none) and (pointer: coarse) { 
        @content;
    }
}
@mixin screen-controller { /* Nintendo Wii controller, Microsoft Kinect */
    @media (hover: hover) and (pointer: coarse) {
        @content;
    }
}

@mixin hover-mobile {
    @media (hover: none) {
        @content;
    }
}
@mixin hover-cursor {
    @media (hover: hover) {
        @content;
    }
}

