@use "../../helpers";
@use "../../resources";
@use "../../resources/appearance/effects";
@use "../../resources/layout/layouts";
@use "../../resources/layout/breakpoints";
@use "../basic";
@use "../components";
@use "../../resources/layout/sizes";

#navbar {
    @include breakpoints.min {
        border: var(--line-width) solid var(--col-grey);
        /* width: 100%; */
        
        background-color: var(--col-white);
        @include effects.drop-out();

        z-index: 1;

        > .content {
            position: relative;

            display: flex;
            justify-content: space-between;
            align-items: center;
            flex-wrap: wrap;

            // row-gap: 1em;

            box-sizing: border-box;

            margin: auto auto;
            width: 100%;
            max-width: var(--max-article-width);
            // padding: 1em;
        }

        > .content > * {
            position: relative;
            // display: flex;
            // display: block;

            flex-basis: 100%;

            &.left-navbar {
                position: relative;
                display: flex;
                justify-content: space-between;
                align-items:flex-end;

                padding: 1rem;

                // background-color: var(--col-white);
                // @include effects.drop-out;

                z-index: 1;

                #navbar-logo {
                    cursor: pointer;
                    
                    width: 2cm;

                    img {
            
                    }
                }

                .toggle {
                    cursor: pointer;
                    user-select: none;
                    -webkit-tap-highlight-color: transparent;

                    appearance: none;
        
                    margin: 1rem;
                    width: 1cm;
                    height: 1cm;

                    background-size: contain;
                    background-repeat: no-repeat;
                    background-position: center;
                }
            }
        
            &.right-navbar {
                position: relative;

                flex-shrink: 1;
                flex-grow: 1;
                // flex-basis: 140px;

                z-index: 0;
                // z-index: -1;
            }

            &.right-navbar .content {
                position: relative;
                display: flex;
                flex-direction: column;
                justify-content: center;
                align-items: stretch;
                text-align: center;

                > * {
                    position: relative;
                    display: inline-block;
                    flex-shrink: 0;

                    // background-color: var(--col-white);
                    // border-top: var(--line-width) solid var(--col-grey-light);
                    // @include effects.drop-out;

                    text-decoration: none;

                    //z-index
                    $total-links: 10;
                    @for $index from 1 through $total-links {
                        &:nth-last-child(#{$index}) {
                            z-index: #{$index - 1};
                        }
                    }

                    > h2 {
                        color: var(--col-purple-dark);
                        text-decoration: none;
            
                        // @at-root #navbar .right-navbar .content a:hover h2 {
                        //     // color: var(--col-gold-dim);
                        //     text-decoration: underline;
                        // }

                    }
                }
            }
        }
    }

    @include breakpoints.sm(true) {
        > .content > * {
            &.left-navbar {
                background-color: var(--col-white);
                @include effects.drop-out;
            }

            &.right-navbar {
                &:not(.-active) {
                    display: none
                }

                .content {
                    > * {
                        background-color: var(--col-white);
                        @include effects.drop-out;
                    }
                }
            }

            &.right-navbar .content {
                > * {
                    &:hover {
                        background-color: var(--col-grey-light);
                    }
                }
            }
        }
    }

    @include breakpoints.md() {
        > .content {
            row-gap: auto;

            align-items: stretch;
        }

        > .content > * {
            flex-basis: auto;

            &.left-navbar {
                #navbar-logo {
                    flex-grow: 1;

                    height: 4cm;
                    width: auto;
                }

                .toggle {
                    display: none;
                }
            }

            &.right-navbar .content {
                flex-direction: row;
                justify-content: flex-end;
                align-items: stretch;

                column-gap: 2em;

                box-sizing: border-box;

                height: 100%;
                padding: 1rem;
        
                > * {
                    display: flex;
                    align-items: center;

                    border: none;

                    &:hover {

                    }

                    > h2 {

                    }
                }
            }
        }
    }
}

