input[type=range] {
  --range-fill: transparent;
  --range-remain: transparent;
  --range-progress: calc((attr(value) - attr(min)) / (attr(max) - attr(min)) * 100%);
  background: linear-gradient(to right, var(--range-fill) 0%, var(--range-fill) var(--range-progress), var(--range-remain) var(--range-progress), var(--range-remain) 100%);
}

:root {
  --col-white: #ffffff;
  --col-black: #000000;
  --col-grey-light:#f3f0f0;
  --col-grey-dark: #555555;
  --col-slate: #99aabc;
  --col-mud: #6d4634;
  --col-mud-light: #b89a5c;
  --col-purple: #551fa7;
  --col-purple-light: #653aa4;
  --col-purple-dark: #573164;
  --col-red-pink: #f45b4f;
  --col-gold: #fbd500;
  --col-gold-dark: #6b5b02;
  --col-gold-dim: #d4af37;
}

:root {
  --line-width: 2px;
  --line-major-width: 4px;
}

:root {
  --drop-col: rgb(0, 0, 0, 0.2);
  --drop-out: 1px 1px 12px var(--drop-col);
  --drop-in: inset var(--drop-out);
}

.responsive-test::before {
  content: "Breakpoint Test: ";
}
.responsive-test::after {
  content: "N/A";
}
@media screen and (min-width: 0) {
  .responsive-test::after {
    color: purple;
    content: "Extra Small (xs)";
  }
}
@media screen and (min-width: 480px) {
  .responsive-test::after {
    color: blue;
    content: "Small (sm)";
  }
}
@media screen and (min-width: 720px) {
  .responsive-test::after {
    color: green;
    content: "Medium (md)";
  }
}
@media screen and (min-width: 960px) {
  .responsive-test::after {
    color: orange;
    content: "Large (lg)";
  }
}
@media screen and (min-width: 1200px) {
  .responsive-test::after {
    color: red;
    content: "Extra Large (xl)";
  }
}
@media screen and (min-width: 1400px) {
  .responsive-test::after {
    color: brown;
    content: "GIGANTIC";
  }
}

.responsive-test-below::before {
  content: "Breakpoint Test (Below): ";
}
.responsive-test-below::after {
  content: "N/A";
}
@media screen and (max-width: 1599px) {
  .responsive-test-below::after {
    color: red;
    content: "Extra Large (xl)";
  }
}
@media screen and (max-width: 1199px) {
  .responsive-test-below::after {
    color: orange;
    content: "Large (lg)";
  }
}
@media screen and (max-width: 959px) {
  .responsive-test-below::after {
    color: green;
    content: "Medium (md)";
  }
}
@media screen and (max-width: 719px) {
  .responsive-test-below::after {
    color: blue;
    content: "Small (sm)";
  }
}
@media screen and (max-width: 479px) {
  .responsive-test-below::after {
    color: purple;
    content: "Extra Small (xs)";
  }
}

:root {
  --max-article-width: 1199px;
  --max-jumbotron-width: 1599px;
  --max-viewer-width: calc(2800px / 2);
  --min-viewer-width: 479px;
  --max-info-width: var(--max-viewer-width);
  --navbar-height: 10em;
  --navbar-max-height: 8em;
  --max-book-info: 6cm;
  --min-book-info: 4cm;
}

* {
  font-family: Roboto;
  color: var(--col-black);
}
.-theme-dark * {
  color: var(--col-white);
}

.-theme-dark {
  color: var(--col-white);
}

:is(h1, h2, h3, h4, h5, h6) {
  color: var(--col-gold-dim);
}
.-theme-dark :is(h1, h2, h3, h4, h5, h6) {
  color: var(--col-white);
}

.-theme-dark:is(h1, h2, h3, h4, h5, h6) {
  color: var(--col-white);
}

._title, ._subtitle {
  color: var(--col-gold-dim);
}
.-theme-dark ._title {
  color: var(--col-white);
}

._title.-theme-dark {
  color: var(--col-white);
}

.-theme-dark ._subtitle {
  color: var(--col-white);
}

._subtitle.-theme-dark {
  color: var(--col-white);
}

a {
  color: var(--col-gold-dim);
  text-decoration-thickness: var(--line-width);
}

hr {
  border-width: calc(var(--line-width) / 2);
  border-color: var(--col-black);
  color: var(--col-black);
  background-color: var(--col-black);
}
.-theme-dark hr {
  border-color: var(--col-white);
  color: var(--col-white);
  background-color: var(--col-white);
}

hr.-theme-dark {
  border-color: var(--col-white);
  color: var(--col-white);
  background-color: var(--col-white);
}

.book-player {
  --page-per-view: 2;
  --total-pages: 10;
  --current-page: 1;
  --progress: calc((var(--current-page) - 1) / (var(--total-pages) - 1) * 100%);
}

.book-player {
  cursor: crosshair;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
  position: relative;
}
.book-player > .page > img {
  width: 100%;
}

.book-player .progress {
  pointer-events: none;
  position: absolute;
  bottom: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
}
.book-player .progress .slider {
  pointer-events: auto;
  --range-fill: var(--col-purple-dark);
  --range-remain: transparent;
  cursor: pointer;
  appearance: none;
  position: absolute;
  bottom: calc(var(--progress-min) / -2);
  margin: 0;
  border-bottom-left-radius: 1vh;
  width: 100%;
  height: var(--progress-min);
  outline: none;
  transform: translateY(50%);
  transition: height 30ms ease-in-out;
}
.book-player .progress .slider::-webkit-slider-thumb {
  pointer-events: auto;
  appearance: none;
  width: var(--point-min);
  height: var(--point-min);
  border-radius: 100%;
  background-color: var(--col-purple-dark);
  box-shadow: var(--drop-out);
  transition: width 30ms linear, height 30ms linear;
}
.book-player .progress .point-counter {
  pointer-events: fill;
  cursor: pointer;
  -webkit-tap-highlight-color: transparent;
  position: absolute;
  bottom: 0;
  left: clamp(var(--comp-width) / 2 + 0.5rem, var(--progress), 100% - var(--comp-width) / 2 - 0.5rem);
  display: inline-block;
  font-size: 0.9rem;
  font-weight: bold;
  transform: translateX(-50%);
  opacity: 0;
  transition: opacity 80ms linear, bottom 120ms ease;
}
.book-player .progress .point-counter .head {
  display: grid;
  place-items: center;
  border-radius: 100vh;
  padding: 0.5em;
  background-color: var(--col-purple-dark);
  box-shadow: var(--drop-out);
}
.book-player .progress .point-counter .head > span {
  display: flex;
  justify-content: center;
  align-items: baseline;
  min-width: 8em;
}
.book-player .progress .point-counter .head > span *:not(.divide) {
  display: inline-block;
  flex-basis: 0;
  flex-grow: 1;
  flex-shrink: 1;
  box-sizing: content-box;
  width: 0;
  padding: 0.25em;
  border-color: transparent;
  font-size: 1em;
  font-weight: bold;
  text-align: center;
}
.book-player .progress .point-counter .head > span *:not(.divide).page {
  cursor: text;
  appearance: none;
  box-sizing: content-box;
  margin: 0;
  color: var(--col-gold-dim);
  background-color: transparent;
}
.book-player .progress .point-counter .head > span *:not(.divide).page:focus {
  outline: none;
  box-shadow: none;
  text-decoration: underline var(--line-width) var(--col-gold-dim);
}
.book-player .progress .point-counter .head > span *:not(.divide).page::-webkit-inner-spin-button, .book-player .progress .point-counter .head > span *:not(.divide).page::-webkit-outer-spin-button {
  appearance: none;
}
.book-player .progress .point-counter .head > span *:not(.divide).max {
  border-color: transparent;
}
.book-player .progress .point-counter .head > span .divide {
  flex-basis: 1ch;
  flex-grow: 0;
  flex-shrink: 0;
}
.book-player .progress .point-counter .head > span .divide::after {
  content: "|";
}
.book-player .progress.-touch .point-counter {
  transition: opacity 80ms linear 1.5s, bottom 120ms ease 1.5s;
}
.book-player .progress .zone {
  pointer-events: none;
  position: absolute;
  bottom: 0;
  left: 50%;
  width: 100%;
  height: var(--progress-zone-size);
  transform: translate(-50%, 50%);
}
.book-player .progress .background {
  pointer-events: none;
  position: absolute;
  bottom: 0;
  width: 100%;
  height: var(--progress-area-height);
  background-image: linear-gradient(to top, var(--area), transparent);
  opacity: 0;
  z-index: auto;
  transition: var(--area-anim);
}
.book-player .progress.-near .slider {
  height: var(--progress-near);
  transition: width 40ms ease-in-out;
}
.book-player .progress.-near .slider::-webkit-slider-thumb {
  width: var(--point-near);
  height: var(--point-near);
  transition: width 30ms ease-in-out, height 30ms ease-in-out;
}
.book-player .progress.-near .background {
  opacity: 1;
}
.book-player .progress.-hover .slider, .book-player .progress.-touch .slider {
  height: var(--progress-max);
}
.book-player .progress.-hover .slider::-webkit-slider-thumb, .book-player .progress.-touch .slider::-webkit-slider-thumb {
  width: var(--point-most);
  height: var(--point-most);
  transition: width 40ms linear, height 40ms linear;
}
.book-player .progress.-active .slider {
  height: var(--progress-max);
  filter: drop-shadow(1px 1px 0.4em var(--col-gold));
}
.book-player .progress.-active .slider::-webkit-slider-thumb {
  width: var(--point-max);
  height: var(--point-max);
}
.book-player .progress:is(.-near, .-hover, .-active) .point-counter {
  bottom: 1rem;
  opacity: 1;
  transition: opacity 80ms linear, bottom 120ms ease;
}
@media screen and (max-width: 719px) {
  .book-player .progress.-touch:not(.-active) .slider::-webkit-slider-thumb, .book-player .progress.-near:not(.-touch) .slider::-webkit-slider-thumb {
    background: linear-gradient(to bottom, var(--col-purple-dark) 0%, var(--col-purple-dark) 50%, var(--col-gold-dim) 50%, var(--col-gold-dim) 100%);
  }
}
@media screen and (max-width: 719px) {
  .book-player .progress.-touch:not(.-active).-touch .slider, .book-player .progress.-near:not(.-touch).-touch .slider {
    background: linear-gradient(to bottom, transparent 0%, transparent 50%, var(--col-purple-dark) 50%, var(--col-purple-dark) 100%);
  }
}

.book-player .page-turn {
  position: absolute;
  top: 0;
  width: calc(100% / var(--page-per-view));
  height: 100%;
}
.next .book-player .page-turn {
  right: 0;
}

.book-player.next .page-turn {
  right: 0;
}

.book-player .next .page-turn {
  right: 0;
}

.book-player .page-turn.next {
  right: 0;
}

.last .book-player .page-turn {
  left: 0;
}

.book-player.last .page-turn {
  left: 0;
}

.book-player .last .page-turn {
  left: 0;
}

.book-player .page-turn.last {
  left: 0;
}

.book-player .page-turn > * {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
}
.next .book-player .page-turn > * {
  right: 0;
}

.book-player.next .page-turn > * {
  right: 0;
}

.book-player .next .page-turn > * {
  right: 0;
}

.book-player .page-turn.next > * {
  right: 0;
}

.book-player .page-turn .next > * {
  right: 0;
}

.book-player .page-turn > .next {
  right: 0;
}

.last .book-player .page-turn > * {
  left: 0;
}

.book-player.last .page-turn > * {
  left: 0;
}

.book-player .last .page-turn > * {
  left: 0;
}

.book-player .page-turn.last > * {
  left: 0;
}

.book-player .page-turn .last > * {
  left: 0;
}

.book-player .page-turn > .last {
  left: 0;
}

.book-player .page-turn > *.page-zone {
  width: var(--page-zone-width);
}
.book-player .page-turn > *.background {
  width: var(--page-area-width);
  opacity: 0;
  transition: var(--area-anim);
}
.book-player .page-turn > *.active {
  width: 100%;
  background-color: transparent;
}
.book-player .page-turn > *.arrow {
  width: 20px;
  filter: drop-shadow(var(--drop-out));
  opacity: 0;
  transform: translateY(-50%);
  transition: opacity 80ms linear, left 120ms ease, right 120ms ease;
}

