.scroll-container-x, .scroll-container-x.bit {
  overflow-x: scroll;
  overflow-y: hidden;
  margin-top: -1rem;
  margin-bottom: -1rem;
  padding-top: 1rem;
  padding-bottom: 1rem;
}
.scroll-container-x::-webkit-scrollbar, .scroll-container-x.bit::-webkit-scrollbar {
  display: none;
}
.scroll-container-x.chunk {
  overflow-x: scroll;
  overflow-y: hidden;
  margin-top: -4rem;
  margin-bottom: -4rem;
  padding-top: 4rem;
  padding-bottom: 4rem;
}
.scroll-container-x.chunk::-webkit-scrollbar {
  display: none;
}
.scroll-container-x.partial {
  overflow-x: scroll;
  overflow-y: hidden;
  margin-top: -15vh;
  margin-bottom: -15vh;
  padding-top: 15vh;
  padding-bottom: 15vh;
}
.scroll-container-x.partial::-webkit-scrollbar {
  display: none;
}
.scroll-container-x.segment {
  overflow-x: scroll;
  overflow-y: hidden;
  margin-top: -50vh;
  margin-bottom: -50vh;
  padding-top: 50vh;
  padding-bottom: 50vh;
}
.scroll-container-x.segment::-webkit-scrollbar {
  display: none;
}
.scroll-container-x.screen {
  overflow-x: scroll;
  overflow-y: hidden;
  margin-top: -100vh;
  margin-bottom: -100vh;
  padding-top: 100vh;
  padding-bottom: 100vh;
}
.scroll-container-x.screen::-webkit-scrollbar {
  display: none;
}

.scroll-container-y, .scroll-container-y.bit {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -1rem;
  margin-right: -1rem;
  padding-left: 1rem;
  padding-right: 1rem;
}
.scroll-container-y::-webkit-scrollbar, .scroll-container-y.bit::-webkit-scrollbar {
  display: none;
}
.scroll-container-y.chunk {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -4rem;
  margin-right: -4rem;
  padding-left: 4rem;
  padding-right: 4rem;
}
.scroll-container-y.chunk::-webkit-scrollbar {
  display: none;
}
.scroll-container-y.partial {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -15vw;
  margin-right: -15vw;
  padding-left: 15vw;
  padding-right: 15vw;
}
.scroll-container-y.partial::-webkit-scrollbar {
  display: none;
}
.scroll-container-y.segment {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -50vw;
  margin-right: -50vw;
  padding-left: 50vw;
  padding-right: 50vw;
}
.scroll-container-y.segment::-webkit-scrollbar {
  display: none;
}
.scroll-container-y.screen {
  overflow-x: hidden;
  overflow-y: scroll;
  margin-left: -100vw;
  margin-right: -100vw;
  padding-left: 100vw;
  padding-right: 100vw;
}
.scroll-container-y.screen::-webkit-scrollbar {
  display: none;
}

.wrap-container {
  display: flex;
  align-items: flex-start;
  justify-content: flex-start;
  flex-wrap: wrap;
}
.wrap-container > * {
  flex-basis: auto;
  flex-grow: 0;
  flex-shrink: 0;
}

.page-banner {
  position: relative;
  padding: 1em;
}
.page-banner > div {
  width: 100%;
  max-width: var(--max-article-width);
  margin: auto;
}

.article-content {
  position: relative;
  padding: 1em;
}
.article-content > div {
  width: 100%;
  max-width: var(--max-article-width);
  margin: auto;
}

.rect-link {
  --background: white;
  --icon-size: 2cm;
}

.rect-link {
  display: inline-block;
  padding: 1em;
  padding-bottom: 2em;
  background: var(--background);
  text-decoration: none;
}
.rect-link .icon {
  display: block;
  width: var(--icon-size);
  height: var(--icon-size);
}
.rect-link .title {
  display: block;
  font-size: 2em;
  font-weight: bold;
  text-align: left;
  margin-top: 1rem;
  margin-bottom: 0.5rem;
}
.rect-link .subtitle {
  display: block;
  font-weight: bold;
  text-align: left;
  margin-bottom: 0.75rem;
}
.rect-link .description {
  display: block;
}

@media screen and (min-width: 0) {
  #navbar {
    border: var(--line-width) solid var(--col-grey);
    /* width: 100%; */
    background-color: var(--col-white);
    box-shadow: var(--drop-out);
    z-index: 1;
  }
  #navbar > .content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    box-sizing: border-box;
    margin: auto auto;
    width: 100%;
    max-width: var(--max-article-width);
  }
  #navbar > .content > * {
    flex-basis: 100%;
  }
  #navbar > .content > *.left-navbar {
    display: flex;
    justify-content: space-between;
    align-items: flex-end;
    padding: 1rem;
  }
  #navbar > .content > *.left-navbar #navbar-logo {
    cursor: pointer;
    width: 2cm;
  }
  #navbar > .content > *.left-navbar .toggle {
    cursor: pointer;
    user-select: none;
    -webkit-tap-highlight-color: transparent;
    appearance: none;
    margin: 1rem;
    width: 1cm;
    height: 1cm;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
  }
  #navbar > .content > *.right-navbar {
    flex-shrink: 1;
    flex-grow: 1;
  }
  #navbar > .content > *.right-navbar .content {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: stretch;
    text-align: center;
  }
  #navbar > .content > *.right-navbar .content > * {
    display: inline-block;
    flex-shrink: 0;
    border-top: var(--line-width) solid var(--col-grey-light);
    text-decoration: none;
  }
  #navbar > .content > *.right-navbar .content > * > h2 {
    color: var(--col-purple-dark);
    text-decoration: none;
  }
}
@media screen and (max-width: 719px) {
  #navbar > .content > *.right-navbar:not(.-active) {
    display: none;
  }
