@use "../../helpers/mixins";

.scroll-container-x {
    &, &.bit {
        @include mixins.scroll-container-x(1rem, 1rem);
    }
    &.chunk {
        @include mixins.scroll-container-x(4rem, 4rem);
    }
    &.partial {
        @include mixins.scroll-container-x(15vh, 15vh);
    }
    &.segment {
        @include mixins.scroll-container-x(50vh, 50vh);
    }
    &.screen {
        @include mixins.scroll-container-x(100vh, 100vh);
    }
}

.scroll-container-y {
    &, &.bit {
        @include mixins.scroll-container-y(1rem, 1rem);
