    &.chunk {
        @include mixins.scroll-container-y(4rem, 4rem);
    }
    &.partial {
        @include mixins.scroll-container-y(15vw, 15vw);
    }
    &.segment {
        @include mixins.scroll-container-y(50vw, 50vw);
    }
    &.screen {
        @include mixins.scroll-container-y(100vw, 100vw);
    }
}

