@use "../../helpers";
@use "../../resources";
@use "../../resources/appearance/effects";
@use "../../resources/layout/breakpoints";
@use "../basic";
@use "../components";
// @use "./../functions";

.book-player-info {
    padding: 1em 1em;

    > div {
        display: flex;

        flex-direction: column;
        justify-content: flex-start;
        align-items: center;
    
        max-width: var(--max-info-width);

        justify-content:space-between;
    
        gap: 1em;
    }

    .image {
        margin-bottom: 0.5em;
    
        img {
            display: inline-block;
            vertical-align: top;
            
            @include effects.drop-out(true);
        }
    }

    .info {
        flex-basis: 0;
        flex-grow: 1;
        flex-shrink: 1;
    
        .title {
            text-align: center;

            text-decoration: none;

            > * {
                margin: 0 auto;

                text-decoration: none;
            }

            + hr {
                color: var(--col-white);
            }

            // @include breakpoints.sm {
            //     text-align: left;
            // }
        }

        .tags {
            display: flex;
            justify-content: center;
            flex-wrap: wrap;

            // gap:1em;
            row-gap: 6px;

            padding: 0 calc(var(--line-width) / 2);

            margin-bottom: 1em;

            > * {
                flex-shrink: 0;

                margin: 0 calc(var(--line-width) / -2);
                border-left: var(--line-width) solid var(--col-black);
                border-right: var(--line-width) solid var(--col-black);
                padding: 0 0.5em;

                font-weight: bold;
                text-decoration: none;

                @at-root ._theme-dark.book-player-info .info .tags > * {
                    border-left: var(--line-width) solid var(--col-white);
                    border-right: var(--line-width) solid var(--col-white);
                }
            }
        }

        .description {
            margin-block-end: 0;
            width: calc(100% - 1em);
        }
    }

    @include breakpoints.sm {
        > div {
            flex-direction: row;
            justify-content: center;
            align-items:flex-start;
        }

        .title {
            text-align: left !important;
        }

        .image {
            margin-bottom: 0;
        }

        .tags {
            justify-content: flex-start !important;

            margin-bottom: 0.5em !important;
        }
    }
}

