        img {
            width: 5cm;
        }
    }

    .title {

        > * {
            color: var(--col-white);
        }
    }

    .tags {
        > * {

        }

        .author {

        }

        .date {

        }
    }

    @include breakpoints.sm() {
        @include breakpoints.md(true) {
            .image img {
                width: 3cm !important;
            }
        }
    }
}

.book-player-info.author {
    background-color: var(--col-gold-dim);
    
    .image {


        img {
