            height: 3cm;
            width: 3cm;
            object-fit: cover;
            object-position: center;

            @include breakpoints.sm() {
                @include breakpoints.md(true) {
                    width: 2cm;
                    height: 2cm;
                }
            }
        }
    }

    .title {

        > * {
            color: var(--col-black);
