//CUSTOM ELEMENT SELECTOR
@mixin custom-selector ($selector: null, $pre: null, $post: null) {
    $new-selector: "&";
    @if ($selector != null) {
        $new-selector: $selector;
    }

    @if ($pre != null) {
        $new-selector: $pre + $new-selector;
    }
    @if ($post != null) {
        $new-selector: $new-selector + $post;
    }

    @if ($selector != null) {
        @at-root #{unquote(#{$new-selector})} {
            @content;
        }
    } @else {
        #{unquote(#{$new-selector})} {
            @content;
        }
    }
}

//BREAKPOINT SELECTOR
@mixin custom-breakpoint($amount: 0, $below: null) {
    @if ($below == null) {
        @media (min-width: $amount) {
            @content;
        }
    } @else {
        @media (max-width: $amount) {
            @content;
        }
    }
}

//SCROLL CONTAINERS
//for small mobile devices (invisible scrollbars, touch-screen scrolling for large content)
@mixin scroll-container-x ($reserve-top: 1em, $reserve-bottom: 1em) {
    overflow-x: scroll;
    overflow-y: visible;

    margin-top: $reserve-top * -1;
    margin-bottom: $reserve-bottom * -1;
    padding-top: $reserve-top;
    padding-bottom: $reserve-bottom;

    &::-webkit-scrollbar {
        display: none;
    }
}

@mixin scroll-container-y ($reserve-left: 1em, $reserve-right: 1em) {
    overflow-x: visible;
    overflow-y: scroll;
    
    margin-left: $reserve-left * -1;
    margin-right: $reserve-right * -1;
    padding-left: $reserve-left;
    padding-right: $reserve-right;

    &::-webkit-scrollbar {
        display: none;
    }
}

