@use "../../helpers";
@use "../../resources";
@use "../../resources/appearance/effects";
@use "../../resources/layout/layouts";
@use "../../resources/layout/breakpoints";
@use "../basic";
@use "../components";
@use "../../resources/layout/sizes";

#navbar {
    border: var(--line-width) solid var(--col-grey);
    /* width: 100%; */
    
    background-color: var(--col-white);
    @include effects.drop-out();

    z-index: 1;

    > .content {
        display: flex;
        justify-content: space-between;
        align-items: center;
        flex-wrap: wrap;

        // row-gap: 1em;

        box-sizing: border-box;

        margin: auto auto;
        width: 100%;
        max-width: var(--max-article-width);
        // padding: 1em;
    }

    > .content > * {
        // display: flex;
        // display: block;

        flex-basis: 100%;

        &.left-navbar {
            display: flex;
            justify-content: space-between;
            align-items:flex-end;

            padding: 1rem;

            #navbar-logo {
                cursor: pointer;
                
                width: 2cm;

                img {
        
                }
            }

            .toggle {
                cursor: pointer;
                user-select: none;
                -webkit-tap-highlight-color: transparent;

                appearance: none;
    
                margin: 1rem;
                width: 1cm;
                height: 1cm;

                background-size: contain;
                background-repeat: no-repeat;
                background-position: center;
            }
        }
    
        &.right-navbar {
            flex-shrink: 1;
            flex-grow: 1;
            // flex-basis: 140px;

            // z-index: -1;
        }

        &.right-navbar .content {
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: stretch;
            text-align: center;
    
            > * {
                display: inline-block;
                flex-shrink: 0;

                border-top: var(--line-width) solid var(--col-grey-light);
                // @include effects.drop-out;

                text-decoration: none;

                > h2 {
                    color: var(--col-purple-dark);
                    text-decoration: none;
        
                    // @at-root #navbar .right-navbar .content a:hover h2 {
                    //     // color: var(--col-gold-dim);
                    //     text-decoration: underline;
                    // }

                }
            }
        }
    }

    @include breakpoints.sm(true) {
        > .content > * {
            &.right-navbar .content > *:hover {
                background-color: var(--col-grey-light);
            }

            &.right-navbar:not(.-active) {
                display: none !important;
            }
        }
    }

    @include breakpoints.md() {
        > .content {
            row-gap: auto !important;
        }
    
        > .content > * {
            flex-basis: auto !important;
    
            &.left-navbar {
                
    
                #navbar-logo {
                    flex-grow: 1 !important;

                    height: 4cm !important;
                    width: auto !important;
                }
    
                .toggle {
                    display: none;
                }
            }
        
            &.right-navbar .content {
                flex-direction: row !important;
                justify-content: flex-end !important;
                align-items: stretch !important;

                column-gap: 2em !important;

                padding: 1rem !important;

                > * {
                    border: none !important;

                    > h2 {
                        
                    }
                }
            }
        }
    }
}

